import React, { useEffect, useState } from "react";

function Products({cat}) {
    const [products, setProducts] = useState([])
    useEffect(()=>{
        if(cat.length>0){
            fetch('https://fakestoreapi.com/products/category/'+cat)
            .then(res=>res.json())
            .then(json=>console.log(json))
        }
        else{
        () => {
            fetch('https://fakestoreapi.com/products?limit=10')
                .then(res => res.json())
                .then(json => setProducts(json))
        }}}
        , [])
    return (
        <div className="flex  flex-wrap gap-5 justify-evenly  h-full">

            {products.map((product) => {
                return (<div className=" relative w-60 h-fit text-center text-xl border-2 border-orange-300">
                    <img className="h-60 w-60 p-2" src={product.image} alt="" />
                    <div className="bg-orange-300 flex flex-col">
                    <span>{product.title}</span>
                    <span >${product.price}</span>                    
                </div>
                </div>
                )
            })}
        </div>
    )
}

export default Products
