import React, { useEffect, useState } from "react";

function Products({cat, searchedProduct}) {
    const [products, setProducts] = useState([])
    useEffect(()=>{
        if(cat.length>0){
            fetch('https://fakestoreapi.com/products/category/'+cat)
            .then(res=>res.json())
            .then(json=>{var newproduct = json.filter((e)=> e.title.toLowerCase().includes(searchedProduct.toLowerCase()) )
            newproduct && setProducts(newproduct)})
        }
        else{
                 fetch('https://fakestoreapi.com/products?limit=10')
                .then(res => res.json())
                .then(json => {
                    var newproduct = json.filter((e)=> e.title.toLowerCase().includes(searchedProduct.toLowerCase()) )
                    newproduct && setProducts(newproduct)

                })
        }}
        , [cat, searchedProduct])

    return (
        <div className="flex  flex-wrap gap-5 justify-evenly  h-full">

            {products.map((product) => {
                return (<div className=" relative w-60 h-70 text-center text-xl border-2 border-orange-300">
                    <img className="h-60 w-60 p-2" src={product.image} alt="" />
                    <div className="bg-orange-300 flex flex-col h-20">
                    <span>{product.title}</span>
                    <span >${product.price}</span>                    
                </div>
                </div>
                )
            })}
        </div>
    )
}

export default Products
