import React, { useEffect,useState } from "react";

function Categories(){
    const [categories,setCategories] = useState([])
    const [category,selectCategories] = useState([])


    useEffect(  
    ()=>{fetch('https://fakestoreapi.com/products/categories')
            .then(res=>res.json())
            .then(json=>setCategories(json))}
    ,[])
    function handleClick(){
        selectCategories(category)

    }
    return(
        <div className="flex flex-col gap-5 justify-evenly h-full">
            <h1 className="text-center text-3xl underline">Categories</h1>
            {categories.map((category)=>{
                return (<div onClick={handleClick} className="bg-orange-400 h-10 text-center w-[75%] p-2 rounded-3xl text-xl m-auto">
                    {category}
                    </div>
                )})}
        </div>
)}

export default Categories
