import React, { useEffect, useState } from "react";

function Products() {
    const [products, setProducts] = useState([])
    useEffect(
        () => {
            fetch('https://fakestoreapi.com/products?limit=10')
                .then(res => res.json())
                .then(json => setProducts(json))
        }
        , [])
    return (
        <div className="flex  flex-wrap gap-5 justify-evenly  h-full">

            {products.map((product) => {
                console.log(product);
                return (<div className="w-60 h-fit text-center p-2 rounded-3xl text-xl ">
                    <img className="h-60 w-60" src={product.image} alt="" />
                    <div className="flex flex-col">
                        <span>{product.title}</span>
                        <span className="absolute bottom-0 ">{product.price}</span>
                    </div>
                </div>
                )
            })}
        </div>
    )
}

export default Products
