import React from 'react'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage(){
    const [username, uName] = useState("");
  const [pass, userPassword] = useState("");
  const navigate = useNavigate();
  const [loggedIn, setLogin] = useState(true);

  function userName(event) {
    uName(event.target.value);
  }

  const [passwordType, setPasswordType] = useState(true)
  function typeChange(){
    setPasswordType(!passwordType)
  }


  function password(event) {
    userPassword(event.target.value);
  }  


  return (
        <div className='flex justify-center items-center h-{'>
             <div className='flex flex-col gap-10 justify-center items-center '>
            <h1 >Welcome Back</h1>
        <form >
            <label htmlFor="uname">Username:</label>
              <input 
                onChange={userName}
                id="uname"
                placeholder="Enter Username "
              />
            <label htmlFor="password">Password:</label>
              <input
                onChange={password}
                id="password"
                placeholder="Enter Password "
                type={passwordType?"password":""}
              />
              <img onClick={typeChange} className={`h-10 w-10 ${passwordType?"hidden":"block"}`} src="/eye.png" alt="" />
              <img onClick={typeChange} className={`h-6 w-10 ${passwordType?"block":"hidden"}`} src="/open-eye.png" alt="" />
            <p className={`text-red-600 text-center ${loggedIn ? "hidden" : "block"}`}>
              Enter valid Username or Password
            </p>
            <button type="submit">Login</button>
            </form>
            <button  ><img src="/close.png" alt="" /></button>
      </div>   
        </div>

  );
}
export default LoginPage
