import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import Counter from './Counter'


function CartItems() {
  const location = useLocation()
  const id = location.state
  const [product, setDetails] = useState([])
  const [count, setPrice] = useState(0)


      let items = JSON.parse(localStorage.getItem("cartitems")) || [];
        if(!items.includes(id)){
          items.push(id);
          localStorage.setItem("cartitems", JSON.stringify(items))
        } 
  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/`)
      .then(res => res.json())
      .then(json => setDetails(json))
  },[])

const cartitems = product.filter(prod => items.includes(prod.id.toString()));



  return (
    <div className='flex flex-col justify-center items-center h-[80vh] w-full pt-40 gap-5 overflow-y-scroll no-scrollbar'>

      {cartitems.map((cartitems) => {
                return (<div  className="flex w-[80%] h-36 gap-5 text-center text-xl border-2 justify-between items-center cursor-pointer px-3">
                    <img className="h-32 w-40 p-2" src={cartitems.image} alt="" />
                    <span className="truncate w-[50%]">{cartitems.title}</span>
                    <span >${cartitems.price }</span>                   
                    <Counter setPrice = {setPrice}/>
                </div>
                )
            })}

    </div>
  )
}

export default CartItems
