import React from 'react'

function LoginPage(){
    const [username, uName] = useState("");
  const [pass, userPassword] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [loggedIn, setLogin] = useState(true);

  function userName(event) {
    uName(event.target.value);
  }
  useEffect(() => {
    dispatch(fetchLiveScan());
  }, [dispatch]);
  const live = useSelector((state) => state.live);


  const [passwordType, setPasswordType] = useState(true)
  function typeChange(){
    setPasswordType(!passwordType)
  }


  function password(event) {
    userPassword(event.target.value);
  }

  const verify = async (e) => {
    e.preventDefault();
    dispatch(fetchLoginData(username));
    let serverData = await axios.get(
      `http://localhost:3005/users?userName=${username}`
    );


    if (serverData.data.length === 0){
      setLogin(false)
    }
        else{
      if (serverData.data[0].password === pass) {
        let liveMessage = live.scans.filter(
        (data) => data.userName === serverData.data[0].userName
      );
      let current = {
          userName: serverData.data[0].userName,
          password: serverData.data[0].password,
          profile: serverData.data[0].profile,
          email: serverData.data[0].email,
          message: liveMessage[0].message,
        };
        dispatch(loginActions.addCurrentUser(current));
        navigate("/home");
      } else {
        setLogin(false);
      }

    } 

  };

  return (
    <div className="flex">
      <div className="bg-white opacity-50  h-full  w-full absolute top-0 left-0"></div>
      <div className="absolute translate-y-1/2 py-10 ipad:left-[30%] w-[100%]  bg-white  ipad:w-[500px] rounded-3xl shadow-[0_3px_10px_rgb(0,0,0,0.2)]">
        <div className="flex flex-col justify-center items-center gap-8">
          <img className="h-16" src="./assets/icons/logo-with-heading.png" alt="" />
          <h1 className="text-2xl">Welcome Back</h1>
          <form onSubmit={verify}>
            <div className="flex gap-3 justify-evenly items-center text-sm mr-14">
              <label className="text-sm " htmlFor="uname">Username:</label>
              <input 
                onChange={userName}
                id="uname"
                className="block my-2 h-10 border-gray-500 border-2 w-40 pl-3 rounded-xl"
                placeholder="Enter Username "
              />
            </div>
            <div className="flex gap-3 justify-evenly items-center text-sm">
              <label htmlFor="password">Password:</label>
              <input
                onChange={password}
                id="password"
                className="block my-2 w-40 h-10 border-gray-500 border-2 pl-3 rounded-xl"
                placeholder="Enter Password " type={passwordType?"password":""}
              />
              <img onClick={typeChange} className={`h-10 w-10 ${passwordType?"hidden":"block"}`} src="./assets/icons/eye.png" alt="" />
              <img onClick={typeChange} className={`h-6 w-10 ${passwordType?"block":"hidden"}`} src="./assets/icons/open-eye.png" alt="" />
            </div>
            <p className={`text-red-600 text-center ${loggedIn ? "hidden" : "block"}`}>
              Enter valid Username or Password
            </p>
            <div className="flex justify-center gap-16 items-center text-xl mt-9">
              <button type="submit" className="bg-primaryGreen py-2 px-32 rounded-3xl text-white">Login</button>

            </div>
          </form>
        </div>
        <button onClick={() => change()} className="absolute right-5 top-5"><img src="./assets/icons/close.png" alt="" /></button>
      </div>
    </div>
  );
}
export default LoginPage
