import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'


function CartItems() {
  const location = useLocation()
  const id = location.state
  const [product, setDetails] = useState([])

      let items = JSON.parse(localStorage.getItem("cartitems")) || [];
        if(!items.includes(id)){
          items.push(id);
          localStorage.setItem("cartitems", JSON.stringify(items))
        } 
  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/`)
      .then(res => res.json())
      .then(json => setDetails(json))
  },[])

const cartitems = product.filter(prod => items.includes(prod.id.toString()));

  return (
    <div className='flex flex-col justify-center items-center mt-10 gap-5'>

      {cartitems.map((cartitems) => {
                return (<div  className="flex gap-5 text-center text-xl border-2 justify-between cursor-pointer">
                    <img className="h-10 w-10 p-2" src={cartitems.image} alt="" />

                    <span className="truncate">{cartitems.title}</span>
                    <span >${cartitems.price}</span>                    
                \
                </div>
                )
            })}

    </div>
  )
}

export default CartItems
